import React, { Component } from "react";

class TasksFilters extends Component {
	render() {
		return (
			<div>
				<button
					className={
						"btn btn-outline-primary btn-sm mr-1" +
						(this.props.filter === "all" ? " active" : "")
					}
					onClick={() => this.props.updateFilter("all")}
				>
					All
				</button>
				<button
					className={
						"btn btn-outline-primary btn-sm mr-1" +
						(this.props.filter === "active" ? " active" : "")
					}
					onClick={() => this.props.updateFilter("active")}
				>
					Active
				</button>
				<button
					className={
						"btn btn-outline-primary btn-sm mr-1" +
						(this.props.filter === "completed" ? " active" : "")
					}
					onClick={() => this.props.updateFilter("completed")}
				>
					Completed
				</button>
			</div>
		);
	}
}

export default TasksFilters;
