import "bootstrap/dist/css/bootstrap.css";
import "@fortawesome/fontawesome-free/css/all.css";
import "../App.css";
import React, { Component } from "react";
import TasksRemaining from "./TasksRemaining";
import TaskItem from "./TaskItem";
import TasksCheckAll from "./TasksCheckAll";
import TasksFilters from "./TasksFilters";
import TasksClearCompleted from "./TasksClearCompleted";
import { observer } from "mobx-react";
import TaskStore from "../stores/TaskStore";

class App extends Component {
	render() {
		return (
			<main>
				<div className="container py-4">
					<div className="p-2 mb-4 bg-light rounded-3">
						<div className="container-fluid py-5">
							<h1 className="display-5 fw-bold text-center mb-5">
								ToDo List
							</h1>
							<input
								type="text"
								className="new-task w-100 p-2 mb-4 font-weight-light"
								placeholder="Enter new task here"
								ref={TaskStore.newTaskInput}
								onKeyUp={TaskStore.addTask}
							/>
							{TaskStore.tasksFiltered().length === 0 && (
								<div
									className="alert alert-primary"
									role="alert"
								>
									No{" "}
									{TaskStore.filter !== "all"
										? TaskStore.filter
										: ""}{" "}
									tasks found
								</div>
							)}
							{TaskStore.tasksFiltered().map((task) => (
								<TaskItem
									key={task.id}
									task={task}
									completeTask={TaskStore.completeTask}
									deleteTask={TaskStore.deleteTask}
									changeTaskEditing={
										TaskStore.changeTaskEditing
									}
									doneEdit={TaskStore.doneEdit}
									doneEditOnOtherKeys={
										TaskStore.doneEditOnOtherKeys
									}
								/>
							))}

							<div className="extra-container d-flex align-items-center justify-content-between mb-2 pt-3 border-top">
								<TasksCheckAll
									length={TaskStore.tasks.length}
									completeAllTasks={
										TaskStore.completeAllTasks
									}
									checked={TaskStore.isAllCompleted()}
								/>

								<TasksRemaining
									remaining={TaskStore.remainingCount()}
								/>
							</div>
							<div className="extra-container d-flex align-items-center justify-content-between mb-2 pt-3 border-top">
								<TasksFilters
									filter={TaskStore.filter}
									updateFilter={TaskStore.updateFilter}
								/>

								{TaskStore.completedCount() > 0 && (
									<TasksClearCompleted
										clearCompleted={
											TaskStore.clearCompleted
										}
									/>
								)}
							</div>
						</div>
					</div>
				</div>
			</main>
		);
	}
}

export default observer(App);
