import { action, makeObservable, observable } from "mobx";
import React from "react";

class TaskStore {
	idForNewTask = 3;
	filter = "all";
	newTaskInput = React.createRef();
	tasks = [
		{
			id: 1,
			title: "First Task from MobX",
			isComplete: false,
			editing: false,
		},
		{
			id: 2,
			title: "Second Task from MobX",
			isComplete: false,
			editing: false,
		},
	];

	addTask = (event) => {
		if (event.key === "Enter") {
			const newTaskTitle = this.newTaskInput.current.value;

			if (newTaskTitle.trim().length === 0) return;

			this.tasks.push({
				id: this.idForNewTask,
				title: newTaskTitle,
				isComplete: false,
				editing: false,
			});

			this.idForNewTask++;

			// let tasks = [...this.state.tasks];
			// let idForNewTask = this.state.idForNewTask + 1;
			// tasks.push({
			// 	id: idForNewTask,
			// 	title: newTaskTitle,
			// 	isComplete: false,
			// 	editing: false,
			// });

			// this.setState({ tasks, idForNewTask });

			this.newTaskInput.current.value = "";
		}
	};

	deleteTask = (id) => {
		let tasks = [...this.state.tasks];

		let index = tasks.findIndex((t) => t.id === id);

		tasks.splice(index, 1);

		this.setState({ tasks });
	};

	constructor() {
		makeObservable(this, {
			idForNewTask: observable,
			filter: observable,
			tasks: observable,
			addTask: action,
		});
	}
}

const store = new TaskStore();

export default store;
