import React, { Component } from "react";
import TaskStore from "../stores/TaskStore";


class TasksCheckAll extends Component {
	render() {
		return (
			<div>
				{this.props.length > 0 && (
					<label>
						<input
							type="checkbox"
							onChange={(event) =>
								TaskStore.completeAllTasks(event)
							}
							checked={this.props.checked}
						/>{" "}
						Check All
					</label>
				)}
			</div>
		);
	}
}

export default TasksCheckAll;
