import { action, makeObservable, observable } from "mobx";
import React from "react";

class TaskStore {
	idForNewTask = 3;
	filter = "all";
	newTaskInput = React.createRef();
	tasks = [
		{
			id: 1,
			title: "First Task from MobX",
			isComplete: false,
			editing: false,
		},
		{
			id: 2,
			title: "Second Task from MobX",
			isComplete: false,
			editing: false,
		},
	];

	addTask = (event) => {
		if (event.key === "Enter") {
			const newTaskTitle = this.newTaskInput.current.value;

			if (newTaskTitle.trim().length === 0) return;

			this.tasks.push({
				id: this.idForNewTask,
				title: newTaskTitle,
				isComplete: false,
				editing: false,
			});

			this.idForNewTask++;

			// let tasks = [...this.state.tasks];
			// let idForNewTask = this.state.idForNewTask + 1;
			// tasks.push({
			// 	id: idForNewTask,
			// 	title: newTaskTitle,
			// 	isComplete: false,
			// 	editing: false,
			// });

			// this.setState({ tasks, idForNewTask });

			this.newTaskInput.current.value = "";
		}
	};

	deleteTask = (id) => {
		let index = this.tasks.findIndex((t) => t.id === id);

		this.tasks.splice(index, 1);
	};

	completeTask = (task) => {
		task.isComplete = !task.isComplete;
		let index = this.tasks.findIndex((t) => t.id === task.id);

		this.tasks.splice(index, 1, task);
	};

	changeTaskEditing = (task) => {
		task.editing = !task.editing;

		let index = this.tasks.findIndex((t) => t.id === task.id);

		this.tasks.splice(index, 1, task);
	};

	doneEdit = (task, event) => {
		event.persist();
		task.editing = false;
		let newTitle = event.target.value;

		if (newTitle.trim().length !== 0) task.title = newTitle;

		let index = this.tasks.findIndex((t) => t.id === task.id);

		this.tasks.splice(index, 1, task);
	};

	doneEditOnOtherKeys = (task, event) => {
		if (event.key === "Enter") {
			this.doneEdit(task, event);
		} else if (event.key === "Escape") {
			this.changeTaskEditing(task);
		}
	};

	get remainingCount(){
		return this.tasks.filter((task) => !task.isComplete).length;
	};

	get isAllCompleted(){
		return this.remainingCount() === 0;
	};

	completedCount = () => {
		return this.tasks.filter((task) => task.isComplete).length;
	};

	clearCompleted = () => {
		this.tasks = this.tasks.filter((task) => !task.isComplete);
	};

	updateFilter = (filter) => {
		this.filter = filter;
	};

	tasksFiltered = () => {
		if (this.filter === "all") {
			return this.tasks;
		} else if (this.filter === "active") {
			return this.tasks.filter((task) => !task.isComplete);
		} else if (this.filter === "completed") {
			return this.tasks.filter((task) => task.isComplete);
		}

		return this.tasks;
	};

	completeAllTasks = (event) => {
		//event.persist();
		this.tasks.forEach((task) => (task.isComplete = event.target.checked));
	};

	constructor() {
		makeObservable(this, {
			idForNewTask: observable,
			filter: observable,
			tasks: observable,
			addTask: action,
			deleteTask: action,
			completeTask: action,
			changeTaskEditing: action,
			doneEdit: action,
			doneEditOnOtherKeys: action,
			remainingCount: action,
			isAllCompleted: action,
			completedCount: action,
			clearCompleted: action,
			updateFilter: action,
			tasksFiltered: action,
			completeAllTasks: action,
		});
	}
}

const store = new TaskStore();

export default store;
