import React, { Component } from "react";
import { observer } from "mobx-react";
import TaskStore from "../stores/TaskStore";

class TaskItem extends Component {
	render() {
		return (
			<div
				key={this.props.task.id}
				className="d-flex align-items-center justify-content-between mb-2"
			>
				<div className="d-flex align-items-center">
					<i
						role="button"
						className={
							"far" +
							(this.props.task.isComplete
								? " fa-check-square"
								: " fa-square text-muted")
						}
						onClick={() => TaskStore.completeTask(this.props.task)}
					></i>
					{!this.props.task.editing && (
						<div
							className={
								"p-2 ml-4" +
								(this.props.task.isComplete
									? " task-completed text-muted font-weight-lighter"
									: "")
							}
							onDoubleClick={() =>
								TaskStore.changeTaskEditing(this.props.task)
							}
						>
							{this.props.task.title}
						</div>
					)}
					{this.props.task.editing && (
						<input
							type="text"
							autoFocus
							className="p-2 ml-4"
							defaultValue={this.props.task.title}
							onBlur={(event) =>
								this.props.doneEdit(this.props.task, event)
							}
							onKeyUp={(event) =>
								TaskStore.doneEditOnOtherKeys(
									this.props.task,
									event
								)
							}
						/>
					)}
				</div>
				<div className="ml-2">
					<i
						role="button"
						className="fas fa-times text-muted"
						onClick={() =>
							this.props.deleteTask(this.props.task.id)
						}
					></i>
				</div>
			</div>
		);
	}
}

export default TaskItem;
