import "bootstrap/dist/css/bootstrap.css";
import "@fortawesome/fontawesome-free/css/all.css";
import "../App.css";
import React, { Component } from "react";

class App extends Component {
	render() {
		return (
			<main>
				<div className="container py-4">
					<div className="p-2 mb-4 bg-light rounded-3">
						<div className="container-fluid py-5">
							<h1 className="display-5 fw-bold text-center mb-5">
								ToDo List
							</h1>
							<input
								type="text"
								className="new-task w-100 p-2 mb-4 font-weight-light"
								placeholder="Enter new task here"
								ref={this.newTaskInput}
								onKeyUp={this.addTask}
							/>
							{this.tasksFiltered().length === 0 && (
								<div class="alert alert-primary" role="alert">
									Add few tasks first
								</div>
							)}
							{this.tasksFiltered().map((task) => (
								<div
									key={task.id}
									className="d-flex align-items-center justify-content-between mb-2"
								>
									<div className="d-flex align-items-center">
										<i
											role="button"
											className={
												"far" +
												(task.isComplete
													? " fa-check-square"
													: " fa-square text-muted")
											}
											onClick={() =>
												this.completeTask(task)
											}
										></i>
										{!task.editing && (
											<div
												className={
													"p-2 ml-4" +
													(task.isComplete
														? " task-completed text-muted font-weight-lighter"
														: "")
												}
												onDoubleClick={() =>
													this.changeTaskEditing(task)
												}
											>
												{task.title}
											</div>
										)}
										{task.editing && (
											<input
												type="text"
												autoFocus
												className="p-2 ml-4"
												defaultValue={task.title}
												onBlur={(event) =>
													this.doneEdit(task, event)
												}
												onKeyUp={(event) =>
													this.doneEditOnEnter(
														task,
														event
													)
												}
											/>
										)}
									</div>
									<div className="ml-2">
										<i
											role="button"
											className="fas fa-times text-muted"
											onClick={() =>
												this.deleteTask(task.id)
											}
										></i>
									</div>
								</div>
							))}

							<div className="extra-container d-flex align-items-center justify-content-between mb-2 pt-3 border-top">
								<div>
									{this.state.tasks.length > 0 && (
										<label>
											<input
												type="checkbox"
												onChange={(event) =>
													this.completeAllTasks(event)
												}
												checked={this.isAllCompleted()}
											/>{" "}
											Check All
										</label>
									)}
								</div>
								<div className="mb-2">
									{this.remainingCount()} item
									{this.remainingCount() > 1 ? "s" : ""} left
								</div>
							</div>
							<div className="extra-container d-flex align-items-center justify-content-between mb-2 pt-3 border-top">
								<div>
									<button
										className={
											"btn btn-outline-primary btn-sm mr-1" +
											(this.state.filter === "all"
												? " active"
												: "")
										}
										onClick={() => this.updateFilter("all")}
									>
										All
									</button>
									<button
										className={
											"btn btn-outline-primary btn-sm mr-1" +
											(this.state.filter === "active"
												? " active"
												: "")
										}
										onClick={() =>
											this.updateFilter("active")
										}
									>
										Active
									</button>
									<button
										className={
											"btn btn-outline-primary btn-sm mr-1" +
											(this.state.filter === "completed"
												? " active"
												: "")
										}
										onClick={() =>
											this.updateFilter("completed")
										}
									>
										Completed
									</button>
								</div>
								{this.completedCount() > 0 && (
									<div>
										<button
											onClick={this.clearCompleted}
											className="btn btn-outline-success btn-sm"
										>
											Clear Completed
										</button>
									</div>
								)}
							</div>
						</div>
					</div>
				</div>
			</main>
		);
	}

	state = {
		idForNewTask: 3,
		filter: "all",
		tasks: [],
	};

	newTaskInput = React.createRef();
	addTask = (event) => {
		if (event.key === "Enter") {
			const newTaskTitle = this.newTaskInput.current.value;

			let tasks = [...this.state.tasks];
			let idForNewTask = this.state.idForNewTask + 1;
			tasks.push({
				id: idForNewTask,
				title: newTaskTitle,
				isComplete: false,
				editing: false,
			});

			this.setState({ tasks, idForNewTask });

			this.newTaskInput.current.value = "";
		}
	};

	deleteTask = (id) => {
		let tasks = [...this.state.tasks];

		let index = tasks.findIndex((t) => t.id === id);

		tasks.splice(index, 1);

		this.setState({ tasks });
	};

	completeTask = (task) => {
		task.isComplete = !task.isComplete;
		let tasks = [...this.state.tasks];
		let index = tasks.findIndex((t) => t.id === task.id);

		tasks.splice(index, 1, task);

		this.setState({ tasks });
	};

	changeTaskEditing = (task) => {
		task.editing = !task.editing;
		let tasks = [...this.state.tasks];
		let index = tasks.findIndex((t) => t.id === task.id);

		tasks.splice(index, 1, task);

		this.setState({ tasks });
	};

	doneEdit = (task, event) => {
		event.persist();
		task.editing = false;
		let newTitle = event.target.value;

		if (newTitle.trim().length !== 0) task.title = newTitle;

		let tasks = [...this.state.tasks];
		let index = tasks.findIndex((t) => t.id === task.id);

		tasks.splice(index, 1, task);

		this.setState({ tasks });
	};

	doneEditOnEnter = (task, event) => {
		if (event.key === "Enter") {
			this.doneEdit(task, event);
		} else if (event.key === "Escape") {
			this.changeTaskEditing(task);
		}
	};

	remainingCount = () => {
		return this.state.tasks.filter((task) => !task.isComplete).length;
	};

	isAllCompleted = () => {
		return this.remainingCount() === 0;
	};

	completedCount = () => {
		return this.state.tasks.filter((task) => task.isComplete).length;
	};

	clearCompleted = () => {
		let tasks = [...this.state.tasks];

		tasks = tasks.filter((task) => !task.isComplete);

		this.setState({ tasks });
	};

	updateFilter = (filter) => {
		this.setState({ filter: filter });
	};

	tasksFiltered = () => {
		let tasks = [...this.state.tasks];

		if (this.state.filter === "all") {
			return tasks;
		} else if (this.state.filter === "active") {
			return tasks.filter((task) => !task.isComplete);
		} else if (this.state.filter === "completed") {
			return tasks.filter((task) => task.isComplete);
		}

		return tasks;
	};

	completeAllTasks = (event) => {
		event.persist();

		let tasks = [...this.state.tasks];
		tasks.forEach((todo) => (todo.isComplete = event.target.checked));

		this.setState({ tasks });
	};
}

export default App;
