import "bootstrap/dist/css/bootstrap.css";
import "@fortawesome/fontawesome-free/css/all.css";
import "./App.css";
import { Component } from "react";

class App extends Component {
	render() {
		return (
			<main>
				<div className="container py-4">
					<div className="p-2 mb-4 bg-light rounded-3">
						<div className="container-fluid py-5">
							<h1 className="display-5 fw-bold text-center mb-5">
								ToDo List
							</h1>
							<div className="Todo-container">
								<input
									type="text"
									className="new-todo w-100 p-2 mb-4 font-weight-light"
									placeholder="Enter new task here"
								/>
								<div className="d-flex align-items-center justify-content-between mb-4">
									<div className="d-flex align-items-center">
										<i
											role="button"
											className="far fa-square text-muted"
										></i>
										<div className="p-2 ml-4">
											This is a task
										</div>
									</div>
									<div className="ml-2">
										<i
											role="button"
											className="fas fa-times text-muted"
										></i>
									</div>
								</div>
							</div>
						</div>
					</div>
				</div>
			</main>
		);
	}

	state = {
		todos: [
			{
				id: 1,

			}
		]
	};
}

export default App;
