import { makeObservable, observable } from "mobx";
class TaskStore {
	idForNewTask = 3;
	filter = "all";
	tasks = [
		{
			id: 1,
			title: "First Task from MobX",
			isComplete: false,
			editing: false,
		},
	];

	constructor() {
		makeObservable(this, {
			idForNewTask: observable,
			filter: observable,
			tasks: observable,
		});
	}
}

const store = new TaskStore();

export default store;
