import React, { Component } from "react";

class TaskItem extends Component {
	render() {
		return (
			<div
				key={task.id}
				className="d-flex align-items-center justify-content-between mb-2"
			>
				<div className="d-flex align-items-center">
					<i
						role="button"
						className={
							"far" +
							(task.isComplete
								? " fa-check-square"
								: " fa-square text-muted")
						}
						onClick={() => this.completeTask(task)}
					></i>
					{!task.editing && (
						<div
							className={
								"p-2 ml-4" +
								(task.isComplete
									? " task-completed text-muted font-weight-lighter"
									: "")
							}
							onDoubleClick={() => this.changeTaskEditing(task)}
						>
							{task.title}
						</div>
					)}
					{task.editing && (
						<input
							type="text"
							autoFocus
							className="p-2 ml-4"
							defaultValue={task.title}
							onBlur={(event) => this.doneEdit(task, event)}
							onKeyUp={(event) =>
								this.doneEditOnEnter(task, event)
							}
						/>
					)}
				</div>
				<div className="ml-2">
					<i
						role="button"
						className="fas fa-times text-muted"
						onClick={() => this.deleteTask(task.id)}
					></i>
				</div>
			</div>
		);
	}
}

export default TaskItem;
